def get_keyword(ciphertext, key_len):
    def score(text):
        return sum([1 for i in text if i in "AEHIORS"]) / len(text)

    # fill ciphertext with @ to make an even break, then group by key character
    ciphertext += "@" * (key_len - len(ciphertext) % key_len)
    text_by_shift = [
        "".join([ciphertext[i + s] for i in range(0, len(ciphertext), key_len)])
        for s in range(key_len)
    ]
    # iterate every group and test each individually
    key = ""
    for shift_text_raw in text_by_shift:
        # remove filler
        shift_text = shift_text_raw.replace("@", "")
        # build ciphers with all letters and score all, keep highest
        best_score = 0
        for code_letter_ord in range(65, 91):
            decoded_text = "".join(
                [
                    chr((ord(char) - code_letter_ord - 130) % 26 + 65)
                    for char in shift_text
                ]
            )
            cipher_score = score(decoded_text)
            if cipher_score > best_score:
                best_score = float(cipher_score)
                key_char = chr(code_letter_ord)
        key += key_char
    return key


def encode(ciphertext, key):
    key = ((len(ciphertext) // len(key) + 1) * key)[: len(ciphertext)]
    return "".join(
        [
            chr((ord(char) + ord(shift) - 130) % 26 + 65)
            for char, shift in zip(ciphertext, key)
        ]
    )


def decode(ciphertext, key):
    key = ((len(ciphertext) // len(key) + 1) * key)[: len(ciphertext)]
    return "".join(
        [
            chr((ord(char) - ord(shift) - 130) % 26 + 65)
            for char, shift in zip(ciphertext, key)
        ]
    )


# a = encode("HELLOWORLD", "ABCXYZ")
b = decode(
    "",
    "CODEWARS",
)
c = get_keyword(
    "",
    8,
)  # AGS, BFE, CTX
